#!/usr/bin/env node

const args = process.argv
const action = args[2]
const path = require('path')
const fsExtra = require('fs-extra')
const APP_PATH = process.cwd()

if (!fsExtra.existsSync(path.resolve(APP_PATH, 'skel'))) {
  process.env.APP_PATH = APP_PATH
}

const databaseHarness = require('../src/databaseHarness')

const run = () => {
  const storeName = args[2]
  console.log(`Back says, ${storeName || 'here we go'}...`)
  switch (action) {
    default:
      databaseHarness(storeName)
  }
}

run()
