const storeConfig = require('./storeConfig')
const storeInit = require('./storeInit')
const step = require('./database/step')
const databaseConfig = require('./database/config')

const run = (storeName = 'primary') => {
  storeConfig(storeName).then(config => {
    const storeAdapter = storeInit(config)
    step(databaseConfig.getSteps(storeName), storeAdapter, config)
  })
}

module.exports = run
