const DEFAULT_LIMIT = 50
const DEFAULT_START = 0

const getParse = {
  filters({ query }) {
    const filters = {}
    Object.keys(query).forEach(name => {
      if (!/^_/.test(name)) {
        filters[name] = query[name]
      }
    })
    return filters
  },
  slice({ query }) {
    // paginate
    return {
      limit: query._limit || DEFAULT_LIMIT,
      start: query._start || DEFAULT_START
    }
  }
}

module.exports = getParse
