const expressHarness = require('./expressHarness')
const storeConfig = require('./storeConfig')
const storeInit = require('./storeInit')
let config = null

module.exports = {
  init: async () => {
    config = await storeConfig()
    storeInit(config)
  },
  server: () => {
    return expressHarness({ api: config.api })
  }
}
